package lecture2;

public class pattern1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int row, n, nst, nsp ;
		nst = 5;
		row = 1;
		n = 4;
		while (row <= n) {
			int csp = 1;

			while (csp<= nsp) {
				System.out.println(" ");
				cst++;
			}
			// System.out.println();
			System.out.println(" ");
			row++;
			nst--;
		}
	}

}
